mapper.update:
UPDATE T_msg
SET readDate=sysdate
WHERE num=#{num}

UPDATE T_msg
SET saved='yes'
WHERE num=#{num}

UPDATE T_msg
SET savedDel=null
WHERE num=#{num}

UPDATE T_msg
SET recDel='yes'
WHERE NUM=#{num}

UPDATE T_msg
SET sendDel='yes'
WHERE NUM=#{num}

UPDATE T_msg
SET savedDel='yes'
WHERE NUM=#{num}

UPDATE T_msg
SET saved=null
WHERE NUM=#{num}
