mapper.update:
UPDATE T_user
SET profile=#{profile}
WHERE userid=#{userid}

UPDATE T_user
SET pwd=#{pwd}
WHERE userid=#{userid}

mapper.select:
SELECT* FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT e.empno, e.ename, e.pnum, u.userid, u.email, TO_CHAR(hdate, 'YYYY-MM-DD') AS hdate FROM T_emp e LEFT JOIN T_user u ON e.empno=u.empno
WHERE e.ename LIKE '%'||#{ename}||'%'
ORDER BY empno ASC) result1) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}
