mapper.select:
SELECT* FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT e.empno, e.ename, e.pnum, u.userid, u.email, TO_CHAR(hdate, 'YYYY-MM-DD') AS hdate FROM T_emp e LEFT JOIN T_user u ON e.empno=u.empno WHERE e.isresign is null
AND e.ename LIKE '%'||#{ename}||'%'
ORDER BY empno ASC) result1) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

SELECT* FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT e.empno, e.ename, e.pnum, u.userid, u.email, TO_CHAR(hdate, 'YYYY-MM-DD') AS hdate, TO_CHAR(rdate, 'YYYY-MM-DD') AS rdate FROM T_emp e LEFT JOIN T_user u ON e.empno=u.empno WHERE e.isresign is not null
AND e.ename LIKE '%'||#{ename}||'%'
ORDER BY empno ASC) result1) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE T_emp
SET isresign = 'yes'
WHERE empno=#{empno}

UPDATE T_emp
SET rdate=sysdate
WHERE empno=#{empno}
